function showSidebar() {
    const sidebar = document.querySelector('.sidebar_mobile');
    const icons_menu = document.querySelector('.header__mobile-body-right-icon');
    const icons_sclose = document.querySelector('.header__mobile-body-right-icon-close');
    sidebar.classList.add('active_mobile');
    sidebar.classList.remove('inactive_mobile');
    icons_menu.style.display = "none";
    icons_sclose.style.display = "block"
}

function hidenSidebar() {
    const sidebar = document.querySelector('.sidebar_mobile');
    const icons_menu = document.querySelector('.header__mobile-body-right-icon');
    const icons_sclose = document.querySelector('.header__mobile-body-right-icon-close');
    sidebar.classList.add('inactive_mobile');
    setTimeout(() => {
        sidebar.classList.remove('active_mobile')
    }, 500);
    icons_menu.style.display = "block";
    icons_sclose.style.display = "none"
}

function showShelectOption() {
    const select = document.querySelector('.sidebar_mobile_li');
    const option = document.querySelector('.sidebar_mobile_li_ul')
    if (option.style.display === "none" || option.style.display === "") {
        option.style.display = "block";
        select.style.borderBottom = "0px"
    } else {
        option.style.display = "none";
        select.style.borderBottom = ""
    }
}

function checkImgMobile() {
    let baiVietElement = document.getElementById('bai-viet'); 
    if (!baiVietElement || window.innerWidth > 999) 
        return; let elements = document.querySelectorAll('.glow-on-hover'); 
    let imgElements = baiVietElement.getElementsByTagName('img'); 
    let h2Elements = baiVietElement.getElementsByTagName('h2'); 
    let h3Elements = baiVietElement.getElementsByTagName('h3'); 
    if (elements) { 
        elements.forEach(element => { 
            element.classList.add('glow-on-hover')
        }) }
    Array.from(imgElements).forEach(img => {
        const src = img.getAttribute('src'); 
        if (src && !src.startsWith('/ckfinder/userfiles/images/Icon')) { 
            img.classList.add('img-responsive') 
        }
        if (img.src.startsWith('https://bt.phongkhamchuyenkhoaandong.vn/ckfinder/userfiles/images/Chat/Chat-Dakhoa.gif')) { 
        // if (img.src.startsWith('http://localhost/ckfinder/userfiles/images/Chat/Chat-Dakhoa.gif')) { 
            img.classList.add('img-gif'); 
            let divWrapper = document.createElement('p'); 
            // divWrapper.className = 'glow-on-hover'; 
            img.parentNode.insertBefore(divWrapper, img); 
            divWrapper.appendChild(img) 
        }
    }); 
    Array.from(h2Elements).forEach(h2 => { 
        h2.classList.add('h2-custom') 
    }); 
    Array.from(h3Elements).forEach(h3 => { 
        h3.classList.add('h3-custom') 
    })
}
checkImgMobile()
document.addEventListener('DOMContentLoaded', checkImgMobile); window.addEventListener('resize', checkImgMobile)